mapper.insert:
INSERT INTO COURSE3 (
	COURSE_NO, 				
	COURSE_TITLE, 	COURSE_DESC, 	COURSE_CATEGORY_NO,  GRADE, 
	COURSE_PRICE,	COURSE_PRICE_SALE,	BEGIN_DATE,		CREATED_AT,	   MEMBER_NO
) VALUES (
	SEQ_COURSE3_NO.NEXTVAL, 
	#{courseTitle},	#{courseDesc},	#{courseCategoryNo}, #{grade},
	#{coursePrice},	#{coursePriceSale},	#{beginDate},	#{createdAt},  #{memberNo}
)

mapper.update:
UPDATE COURSE3 
SET 
	COURSE_STATUS = 
		CASE 
			/* 시작일이 없는 경우 준비중(0) */
			WHEN BEGIN_DATE IS NULL THEN 0
			/* 시작일이 미래인 경우 준비중(0) */
			WHEN BEGIN_DATE > SYSDATE THEN 0
			/* 종료일이 있고, 종료일이 지난 경우 완료(2) */
			WHEN END_DATE IS NOT NULL AND SYSDATE > END_DATE THEN 2
			/* 시작일이 지났고, 종료일이 없거나 진행중인 경우 진행중(1) */
			WHEN SYSDATE >= BEGIN_DATE AND (END_DATE IS NULL OR END_DATE >= SYSDATE) THEN 1
		END,
	UPDATED_AT = SYSDATE
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT C.*, COUNT(*) OVER() as total_count, SUM(CASE WHEN C.course_status = 0 THEN 1 ELSE 0 END) OVER() as preparing_count, SUM(CASE WHEN C.course_status = 1 THEN 1 ELSE 0 END) OVER() as in_progress_count, SUM(CASE WHEN C.course_status = 2 THEN 1 ELSE 0 END) OVER() as completed_count FROM COURSE3 C WHERE C.member_no = #{memberNo}
AND ( CASE WHEN #{status} = 'preparing' THEN course_status = 0 WHEN #{status} = 'inProgress' THEN course_status = 1 WHEN #{status} = 'completed' THEN course_status = 2 END )
ORDER BY C.created_at DESC
